css:
  @media print {
    body {
      margin: 0;
    }
    .ticket-container {
      page-break-inside: avoid;
    }
  }

  * {
    text-transform: uppercase;
  }

  body {
    margin: 0;
    padding: 0;
    font-family: sans-serif;
  }

  .ticket-container {
    width: 720px;
    padding: 20px 30px;
    border: 1px dotted #000;
    margin: auto;
    font-size: 12px;
    color: #222;
    box-sizing: border-box;
  }

  .header {
    text-align: center;
    margin-bottom: 10px;
  }

  .logo {
    width: 80px;
    margin-bottom: 5px;
  }

  .company-name {
    font-size: 14px;
    font-weight: bold;
  }

  .tagline {
    font-size: 10px;
    margin-bottom: 2px;
  }

  .contact-info {
    font-size: 9px;
    color: #444;
  }

  .section-title {
    font-size: 12px;
    font-weight: bold;
    border-bottom: 1px dotted #000;
    margin-top: 14px;
    margin-bottom: 4px;
    padding-bottom: 2px;
    text-align: center;
  }

  .field-grid {
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
    margin-bottom: 6px;
  }

  .field-block {
    width: 48%;
    margin-bottom: 6px;
  }

  .field-label {
    font-weight: bold;
    color: #000;
    font-size: 11px;
    display: inline-block;
    margin-right: 4px;
    text-transform: none;
  }

  .field-value {
    font-weight: normal;
    color: #222;
    font-size: 12px;
    display: inline-block;
    text-transform: uppercase;
  }

  .footer {
    text-align: center;
    font-size: 9px;
    margin-top: 12px;
    border-top: 1px dotted #000;
    padding-top: 4px;
    color: #555;
  }
  
  
.section-title {
 font-size: 8px;
 font-weight: bold;
 border-bottom: 1px dotted #000;
 margin-top: 14px;
 margin-bottom: 4px;
 padding-bottom: 2px;

}
